import { createAction, createReducer, on } from "@ngrx/store";
import { environment } from "../../environments/environment";
export const increment = createAction("[counterComponent] 增加计数器值");
export const decrement = createAction("[counterComponent] 减少计数器值");
export const reset = createAction("[counterComponent] 重置计数器值");
export const login = createAction("[counterComponent] 登录");
export const logout = createAction("[counterComponent] 登出");
const initialState = 0;
const counterReducer = createReducer(initialState, on(increment, state => state + 1), on(decrement, state => state - 1), on(reset, state => initialState));
export const reducers = {
  counter: counterReducer
};
export const loggers = reducer => {
  return (state, action) => {
    console.log("此前状态", state);
    console.log("动作", action);
    const newState = reducer(state, action);
    console.log("此后状态", newState);
    return newState;
  };
};
export const metaReducers = !environment.production ? [loggers] : [];
